Use Knjiznica
create table Posudbe
(
	IdPosudbe int primary key identity (1,1),
	DatumPosudbe date,
	DatumVracanja date,
	DatumStvarnogVracanja date,
	IdKnjige int foreign key references Knjige(IdKnjige),
	ClanskiBroj int foreign key references Clanovi(ClanskiBroj),
)

select * from Posudbe



update dbo.POSUDBE SET DatumStvarnogVracanja = null where IdPosudbe =15 ;



use Knjiznica
create view SlobodneKnjige1
as 
select k.IdKnjige, k.NazivKnjige, p.DatumStvarnogVracanja
from dbo.Knjige k inner join dbo.Posudbe p 
on k.IdKnjige=p.IdKnjige
where 
DatumStvarnogVracanja is not null 
or
k.IdKnjige not in (select IdKnjige from dbo.Posudbe);


select * from SlobodneKnjige1


create procedure NeaktivniClanovi
@ClanskiBroj int
AS
select c.ClanskiBroj,c.ImePrezime,c.AdresaMjestoStanovanja,c.BrojTelefona, c.DatumRodjenja,c.DatumUclanjenja 
into NEAKTIVNI 
from dbo.Clanovi c left join dbo.Posudbe p on c.Clanovi.ClanskiBroj= p.Posudbe.ClanskiBroj
where ClanskiBroj=@ClanskiBroj
and DatumPosudbe is null 

exec. dbo.NeaktivniClanovi





